function CheckPass(a){this.MINIMUM_LENGTH=a.minLength;this.MINIMUM_COMBINATION=a.minCombination;this.MAX_ALLOW_SAME_CHARACTER=a.maxAllowSameCharacter;this.MAX_ALLOW_SEQUENCE_CHARACTER=a.maxAllowSequenceCharacter;}CheckPass.prototype.isCombination=function(a){var b=0;-1<a.search(/[a-z]/g)&&b++;-1<a.search(/[A-Z]/g)&&b++;-1<a.search(/[`~!@#$%^&*()_\-+={\[}\]|\\:;"'<,>.?/]/g)&&b++;-1<a.search(/[0-9]/g)&&b++;return a.length<this.MINIMUM_LENGTH||b<this.MINIMUM_COMBINATION?!0:!1};
CheckPass.prototype.isSameSequence=function(a){for(var b=0,c=0;c<a.length-1;c++){var d=a.charAt(c),e=a.charAt(c+1);if(d===e){if(b++,b>=this.MAX_ALLOW_SAME_CHARACTER-1)return!0}else b=0}return!1};
CheckPass.prototype.isStepSequence=function(a){for(var b=0,c=0,d=0;d<a.length-1;d++){var e=a.charAt(d),f=a.charAt(d+1);if(1===e.charCodeAt(0)-f.charCodeAt(0)&&/[a-zA-Z0-9]/.test(e)&&/[a-zA-Z0-9]/.test(f)){if(b++,b>=this.MAX_ALLOW_SEQUENCE_CHARACTER-1)return!0}else b=0;if(-1===e.charCodeAt(0)-f.charCodeAt(0)&&/[a-zA-Z0-9]/.test(e)&&/[a-zA-Z0-9]/.test(f)){if(c++,c>=this.MAX_ALLOW_SEQUENCE_CHARACTER-1)return!0}else c=0}return!1};
CheckPass.prototype.check=function(a){return this.isCombination(a)?1:this.isSameSequence(a)?2:this.isStepSequence(a)?3:0};